CREATE TABLE BASE_ENTITY (
id VARCHAR(36) default UUID(),
CONSTRAINT PK_id PRIMARY KEY (id)
);

CREATE TABLE DESCRIPTABLE (
id VARCHAR(36) default UUID(),
DESCRIPTION VARCHAR(150) default NULL,
CONSTRAINT PK_descriptable_id PRIMARY KEY (id)
);

CREATE TABLE restaurant_schema.RESTAURANT_TYPE ( ID VARCHAR(36) default UUID(), CONSTRAINT PK_RESTAURANT_TYPE_ID PRIMARY KEY (ID) );
CREATE TABLE restaurant_schema.RESTAURANT ( ID VARCHAR(36) default UUID(), DESCRIPTION VARCHAR(150) default NULL, NAME VARCHAR(50) NOT NULL, RESTAURANT_TYPE_ID VARCHAR(36) NOT NULL, -- TODO add NOT NULL on production USER_ID BIGINT DEFAULT NULL, CONSTRAINT PK_RESTAURANT_ID PRIMARY KEY (ID), CONSTRAINT FK_RESTAURANT_RESTAURANT_TYPE FOREIGN KEY (RESTAURANT_TYPE_ID) REFERENCES RESTAURANT_TYPE(ID), CONSTRAINT FK_RESTAURANT_USER FOREIGN KEY (USER_ID) REFERENCES auth.USER(ID) );
CREATE TABLE restaurant_schema.RESTAURANT_TABLE( ID VARCHAR(36) default UUID() PRIMARY KEY, IDENTIFIER_ID VARCHAR(5) NOT NULL, RESTAURANT_ID VARCHAR(36) NOT NULL, CONSTRAINT FK_RESTAURANT_ID FOREIGN KEY (RESTAURANT_ID) REFERENCES RESTAURANT(ID), CONSTRAINT UNIQUE_IDENTIFIER_RESTAURANT_ID UNIQUE (IDENTIFIER_ID,RESTAURANT_ID) );

CREATE TABLE menu_schema.MENU (
ID VARCHAR(36) PRIMARY KEY default UUID(),
NAME VARCHAR(36) NOT NULL,
DESCRIPTION VARCHAR(100),
MENU_TYPE_ID VARCHAR(36) NOT NULL,
RESTAURANT_ID VARCHAR(36),
CONSTRAINT FK_MENU_MENU_TYPE FOREIGN KEY (MENU_TYPE_ID) REFERENCES MENU_TYPE(ID),
CONSTRAINT FK_MENU_RESTAURANT FOREIGN KEY (RESTAURANT_ID) REFERENCES restaurant_schema.RESTAURANT(ID)
);

CREATE INDEX menu_restaurant_index ON menu_schema.MENU (RESTAURANT_ID);

