changeSet.rollback:
DROP schema auth;
drop schema menu_schema;
drop schema restaurant_schema;

changeSet.sql:
CREATE TABLE BASE_ENTITY (
id VARCHAR(36) default UUID(),
CONSTRAINT PK_id PRIMARY KEY (id)
);

CREATE TABLE DESCRIPTABLE (
id VARCHAR(36) default UUID(),
DESCRIPTION VARCHAR(150) default NULL,
CONSTRAINT PK_descriptable_id PRIMARY KEY (id)
);

CREATE TABLE restaurant_schema.RESTAURANT_TYPE (
ID VARCHAR(36) default UUID(),
CONSTRAINT PK_RESTAURANT_TYPE_ID PRIMARY KEY (ID)
);

CREATE TABLE restaurant_schema.RESTAURANT (
ID VARCHAR(36) default UUID(),
DESCRIPTION VARCHAR(150) default NULL,
NAME VARCHAR(50) NOT NULL,
RESTAURANT_TYPE_ID VARCHAR(36) NOT NULL,
CONSTRAINT PK_RESTAURANT_ID PRIMARY KEY (ID),
CONSTRAINT FK_RESTAURANT_RESTAURANT_TYPE FOREIGN KEY (RESTAURANT_TYPE_ID) REFERENCES RESTAURANT_TYPE(ID)
);

CREATE TABLE menu_schema.MENU (
ID VARCHAR(36) PRIMARY KEY,
DESCRIPTION VARCHAR(100),
MENU_TYPE_ID VARCHAR(36) NOT NULL,
CONSTRAINT FK_MENU_MENU_TYPE FOREIGN KEY (MENU_TYPE_ID) REFERENCES MENU_TYPE(ID)
);

