CREATE TABLE menu_schema.MENU_SUBMENU(
MENU_ID VARCHAR(36) NOT NULL,
SUBMENU_ID VARCHAR(36) NOT NULL,
PRICE DECIMAL(7,2) DEFAULT 0,
ON_SALE DECIMAL(7,2) DEFAULT NULL CHECK(ON_SALE >= PRICE),
UNIQUE KEY MENU_SUBMENU_ID (SUBMENU_ID,MENU_ID),
CONSTRAINT FK_MENU_SUBMENU FOREIGN KEY (MENU_ID) REFERENCES MENU(ID),
CONSTRAINT FK_SUBMENU_SUBMENU FOREIGN KEY (SUBMENU_ID) REFERENCES SUBMENU(ID)
);

